				data += '&fOutMode=' + o.fOutMode + '&fIsAjax=1';
			}
			
			if ( "object" == $.type(data) ) {
				var data = $.extend(true, {}, data);
				if ( !data.isAjax ) data.fIsAjax = 1;
				if ( !data.fOutMode ) data.fOutMode = o.fOutMode;
			}
