                $(this).siblings('.gridcontent').slideToggle();
            }
        }
    });
    $('.sectnTop').click(function () {
        if ($(window).width() > 990)
            return;
        var activeBlogSiblingDisplay = $(this).siblings('.sectnMiddle').css('display');
        $('.sectnTop').removeClass('active');
        $('.sectnMiddle').slideUp();

