            $(this).toggleClass("active");
            if ($(window).width() < 990) {
                $(this).siblings('.sectnMiddle').slideToggle();
            }
        }
    });
    $('#user-name').click(function () {
        $('#drop_menu').toggleClass('open_menu');
    });
