    $(document).trigger('close.mbsmessage');
}
var $image;
function cropImage(obj) {
    $image = obj;
    obj.cropper({
        aspectRatio: 1,
        autoCropArea: 0.4545,
        guides: false,
        highlight: false,
        dragCrop: false,
        cropBoxMovable: false,
        cropBoxResizable: false,
        responsive: true,
        crop: function (e) {
            var json = [
                '{"x":' + e.detail.x,
                '"y":' + e.detail.y,
                '"height":' + e.detail.height,
                '"width":' + e.detail.width,
                '"rotate":' + e.detail.rotate + '}'
            ].join();
            $("#img-data").val(json);
        },
        built: function () {
            $(this).cropper("zoom", 0.5);
        },

    })
}

